mapper.select:
select su.* ,sr.role_id ,sr.role_name from dg_sys_user su LEFT JOIN dg_sys_user_role sur on sur.user_id=su.user_id LEFT JOIN dg_sys_role sr on sur.role_id=sr.role_id where 1=1 and su.is_deleted =0
and su.create_time >= #{userVO.startTime}
and su.create_time <= #{userVO.endTime}
and su.phone like CONCAT('%',#{userVO.phone},'%')
and sr.role_id =#{userVO.roleId}
and (sr.role_name like CONCAT('%',#{userVO.keyWords},'%') or su.email like CONCAT('%',#{userVO.keyWords},'%') or su.name like CONCAT('%',#{userVO.keyWords},'%'))

select su.* from dg_sys_user su where 1=1 and su.is_deleted =0
and su.create_time >= #{userVO.startTime}
and su.create_time <= #{userVO.endTime}
and su.phone like CONCAT('%',#{userVO.phone},'%')
and su.name like CONCAT('%',#{userVO.name},'%')
and su.email like CONCAT('%',#{userVO.email},'%')

select su.* from dg_sys_user su ,dg_sys_user_role sur 
where su.user_id=sur.user_id  and  su.is_general_administrator=1 and is_deleted=0 

  and sur.role_id=#{roleId}

select sr.* from dg_sys_role sr where 1=1 and sr.is_deleted=0
and sr.is_used =#{isUsed}
and sr.created_user =#{createdUser}

mapper.update:
update
	dg_sys_user su
left join dg_sys_user_role sur on su.user_id = sur.user_id
	set su.is_used = #{isUsed}
where sur.role_id = #{roleId}
and su.is_deleted = 0
